import React, { Component } from "react";
import './App.css';
import {fetchData} from './utils/Api'
import {parsSales} from './utils/Tools'

const getInfo = async (token) =>  await parsSales(await fetchData(token));


class InputCreds extends Component 
{
    constructor(){
        super();
        this.state = {
            login: 'Login',
            pass: 'Pass',
            card: 'card number',
            sales: ['1']
        };
        this.handleChange = this.handleChange.bind(this);

    }

    handleChange(event) {
        this.setState({city: event.target.value});
    }


    async buttonGetSales() {
        this.setState({sales : await getInfo('5ykemsLE49WuqT0rakO2v066TCgMl2My'),  })
    }
    outputInfo(){
         return(
            <form>
            <label>
              Логин:
              <input type="text" name={this.state.login} onChange={this.handleChange} /><br></br><br></br>
              Пароль:
              <input type="text" name={this.state.pass} onChange={this.handleChange} /><br></br><br></br>
              Номер карты:
              <input type="text" name={this.state.card} onChange={this.handleChange} /><br></br><br></br>
            </label>
            <input type="submit" value="Отправить" />
          </form>
        )   
    }
    render() {

        return (
            <div> 
            <button onClick = { 
                async () => {
                await this.buttonGetSales();
                }
            }> Получить </button><br></br><br></br><br></br><br></br>
           {this.outputInfo()}
        </div>
        );
    }
}  


export {InputCreds};
