import React, { Component } from "react";
import './App.css';
import {fetchData} from './utils/Api'
import {parsSales} from './utils/Tools'

const getInfo = async (token) =>  await parsSales(await fetchData(token));


class InputCreds extends Component 
{
    constructor(){
        super();
        this.state = {
            login: 'Login',
            pass: 'Pass',
            card: 'card number'
        };
        this.handleChange = this.handleChange.bind(this);

    }

    handleChange(event) {
        this.setState({city: event.target.value});
    }


    async buttonGetWeather() {
        this.setState({dataWeather : await getInfo(this.state.city),  })
    }
    outputInfo(){
         return(
            <form>
            <label>
              Имя:
              <input type="text" name="name" />
            </label>
            <input type="submit" value="Отправить" />
          </form>
        )   
    }
    render() {

        return (
            <div> 
            <button onClick = { 
                async () => {
                await this.buttonGetWeather();
                }
            }> Получить </button><br></br><br></br><br></br><br></br>
           {this.outputInfo()}
        </div>
        );
    }
}  


export {InputCreds};
