                    <XAxis dataKey="name" stroke="#000" position="insideBottom"/>
                    <YAxis  unit = "м/с" dataKey="windSpeed" stroke="#000"/>
                    <Tooltip />
                    <Legend />
                    <CartesianGrid stroke="#eee"/>
                    <Line type="monotone" dataKey="windSpeed" stroke="#12b202" activeDot={{ r: 8 }}/>
                </LineChart>
            </div> 
